+    res.json({ message: 'Funcion치rio atualizado com sucesso.', funcionario: userToDTO(updated) });
+  } catch (err) {
+    console.error(err);
+    res.status(500).json({ message: 'Erro ao atualizar funcion치rio.' });
+  }
