+    const target = await User.findById(req.params.id, 'role');
+    if (!target) return res.status(404).json({ message: 'Funcion치rio n칚o encontrado.' });
+
+    if (!(actorRole === 'admin_master' || (actorRole === 'admin' && roleRank[target.role] < roleRank.admin))) {
+      return res.status(403).json({ message: 'Voc칡 n칚o tem permiss칚o para remover este usu치rio do quadro.' });
+    }
+
+    target.role = 'cliente';
