+    if (!userId) return res.status(400).json({ message: 'userId 칠 obrigat칩rio.' });
+    if (!['funcionario', 'admin', 'admin_master'].includes(role)) {
+      return res.status(400).json({ message: 'Cargo inv치lido.' });
+    }
+
+    const actorRole = req.user?.role;
+    const target = await User.findById(userId, 'role');
+    if (!target) return res.status(404).json({ message: 'Usu치rio n칚o encontrado.' });
+
+    if (!canChangeRole(actorRole, target.role, role)) {
+      return res.status(403).json({ message: 'Voc칡 n칚o tem permiss칚o para alterar este cargo.' });
+    }
+
+    target.role = role;
+    await target.save();
+
+    const ret = await User.findById(
+      userId,
+      'nomeCompleto nomeContato razaoSocial email role tipoConta celular cpf cnpj grupos' // +grupos
+    ).lean();
+    res.json({ message: 'Usu치rio transformado com sucesso.', funcionario: userToDTO(ret) });
