+    console.error(err);
+    res.status(500).json({ message: 'Erro ao criar funcion치rio.' });
+  }
+});
+
+// ATUALIZA funcion치rio (policy aplicada para mudar role)
+router.put('/:id', authMiddleware, requireAdmin, async (req, res) => {
