+        return res.status(403).json({ message: 'Voc칡 n칚o tem permiss칚o para alterar este cargo.' });
+      }
+      update.role = role;
+    }
+
+    if (typeof grupos !== 'undefined') {
+      const ALLOWED_GROUPS = ['gerente','vendedor','esteticista','veterinario'];
+      let arr = [];
