+      { new: true, runValidators: true, fields: 'nomeCompleto nomeContato razaoSocial email role tipoConta celular cpf cnpj grupos' }
+    ).lean();
+
+    res.json({ message: 'Funcion치rio atualizado com sucesso.', funcionario: userToDTO(updated) });
+  } catch (err) {
+    console.error(err);
+    res.status(500).json({ message: 'Erro ao atualizar funcion치rio.' });
+  }
+});
+
+// REMOVER do quadro (rebaixa para cliente) com policy
+router.delete('/:id', authMiddleware, requireAdmin, async (req, res) => {
