// servidor/routes/adminFuncionarios.js
const express = require('express');
const router = express.Router();
const User = require('../models/User'); // ajuste o caminho do seu model de usu치rio
const authMiddleware = require('../middleware/authMiddleware');

// 游 Todas as rotas daqui exigem autentica칞칚o e cargo admin/admin_master
function requireAdmin(req, res, next) {
  if (req.user.role === 'admin' || req.user.role === 'admin_master') {
    return next();
  }
  return res.status(403).json({ message: 'Acesso negado. Apenas administradores.' });
}

// 游늷 Listar funcion치rios
router.get('/', authMiddleware, requireAdmin, async (req, res) => {
