    const { nome, email, senha, role } = req.body;

    const existente = await User.findOne({ email });
    if (existente) return res.status(400).json({ message: 'Email j치 cadastrado.' });

    const novo = new User({ nome, email, password: senha, role: role || 'funcionario' });
    await novo.save();

    res.json({ message: 'Funcion치rio criado com sucesso.' });
  } catch (err) {
    console.error(err);
    res.status(500).json({ message: 'Erro ao criar funcion치rio.' });
  }
});

