const express = require('express');
const router = express.Router();
const authMiddleware = require('../middleware/authMiddleware');
const User = require('../models/User'); // supondo que os funcion치rios tamb칠m sejam usu치rios

// Listar todos os funcion치rios
router.get('/', authMiddleware, async (req, res) => {
  try {
    if (req.user.role !== 'admin' && req.user.role !== 'admin_master') {
      return res.status(403).json({ message: 'Acesso negado' });
    }

    const funcionarios = await User.find({ role: { $in: ['funcionario', 'admin'] } });
    res.json(funcionarios);
  } catch (err) {
    res.status(500).json({ message: 'Erro ao buscar funcion치rios' });
  }
});

// Criar novo funcion치rio
router.post('/', authMiddleware, async (req, res) => {
  try {
    if (req.user.role !== 'admin' && req.user.role !== 'admin_master') {
      return res.status(403).json({ message: 'Acesso negado' });
    }

    const { nome, email, senha, role } = req.body;
    const novoFuncionario = new User({ nome, email, senha, role });
    await novoFuncionario.save();

    res.json({ message: 'Funcion치rio criado com sucesso!', funcionario: novoFuncionario });
  } catch (err) {
    res.status(500).json({ message: 'Erro ao criar funcion치rio' });
  }
});

// Atualizar funcion치rio
router.put('/:id', authMiddleware, async (req, res) => {
  try {
    if (req.user.role !== 'admin' && req.user.role !== 'admin_master') {
      return res.status(403).json({ message: 'Acesso negado' });
    }

    const { nome, email, role } = req.body;
    const funcionario = await User.findByIdAndUpdate(req.params.id, { nome, email, role }, { new: true });
    res.json(funcionario);
  } catch (err) {
    res.status(500).json({ message: 'Erro ao atualizar funcion치rio' });
  }
});

// Excluir funcion치rio
router.delete('/:id', authMiddleware, async (req, res) => {
  try {
    if (req.user.role !== 'admin_master') {
      return res.status(403).json({ message: 'Apenas admin_master pode excluir funcion치rios' });
    }

    await User.findByIdAndDelete(req.params.id);
    res.json({ message: 'Funcion치rio removido com sucesso!' });
  } catch (err) {
    res.status(500).json({ message: 'Erro ao remover funcion치rio' });
  }
});

module.exports = router;
