const express = require('express');
const http = require('http');
const { Server } = require("socket.io");
const cors = require('cors');
const dotenv = require('dotenv');
const connectDB = require('./config/db');

// Importar Rotas
const productRoutes = require('./routes/products');
const categoryRoutes = require('./routes/categories');
const authRoutes = require('./routes/auth');
const petRoutes = require('./routes/pets');
const jobRoutes = require('./routes/jobs');
const userRoutes = require('./routes/users');
const cartRoutes = require('./routes/cart');
const favoritesRoutes = require('./routes/favorites');
const storeRoutes = require('./routes/stores');
const vehicleRoutes = require('./routes/vehicles');
const deliveryZoneRoutes = require('./routes/deliveryZones');
const promocoesRoutes = require('./routes/promocoes');
const bannerRoutes = require('./routes/banners');
const addressRoutes = require('./routes/addresses');
const shippingRoutes = require('./routes/shipping');

dotenv.config();
connectDB();

const app = express();
const server = http.createServer(app); 
const io = new Server(server, { 
    cors: {
        origin: "*",
        methods: ["GET", "POST"]
    }
});

// Middleware para passar a inst칙ncia 'io' para as rotas
app.set('socketio', io);

// Middleware
app.use(express.json());
app.use(cors());
app.use(express.static('public'));

// Rotas da API
app.use('/api/products', productRoutes);
app.use('/api/categories', categoryRoutes);
app.use('/api/auth', authRoutes);
app.use('/api/users', userRoutes);
app.use('/api/cart', cartRoutes);
app.use('/api/favorites', favoritesRoutes);
app.use('/api/stores', storeRoutes);
app.use('/api/vehicles', vehicleRoutes);
app.use('/api/delivery-zones', deliveryZoneRoutes);
app.use('/api/promocoes', promocoesRoutes);
app.use('/api/banners', bannerRoutes);
app.use('/api/pets', petRoutes);
app.use('/api/jobs', jobRoutes);
app.use('/api/addresses', addressRoutes);
app.use('/api/shipping', shippingRoutes);

app.use('/api/admin/users', require('./routes/adminUsers'));


// L칩gica do Socket.IO
io.on('connection', (socket) => {
  console.log('Um utilizador conectou-se via WebSocket');
  socket.on('disconnect', () => {
    console.log('Utilizador desconectou-se');
  });
});


const PORT = process.env.PORT || 3000;
server.listen(PORT, () => console.log(`Servidor a rodar (com WebSockets) na porta ${PORT}`));
